USE TSQLV4;

DROP TABLE IF EXISTS dbo.Employees;

CREATE TABLE dbo.Employees

(

    empid INT NOT NULL,

    firstname VARCHAR(30) NOT NULL,

    lastname  VARCHAR(30) NOT NULL,

    hiredate  DATE        NOT NULL,

    mgrid     INT             NULL,

    ssn       VARCHAR(30) NOT NULL,

    salary    MONEY       NOT NULL

);

/*
- USE statement uses context of particular database i.e. TSQLV4
- If the table already exists, DROP IF drops the previous table.
- CREATE TABLE creates a table with a name, for above code its dbo.Employees and the definition of all the attributes in "( )"
- The attributes are assigned according to the variable, VARCHAR() for strings , DATE for MMDDYY, MONEY for a salary.
*/

/*
## **_<u>Data Integrity</u>_**

### **Primary-key Constraints**
*/

ALTER TABLE dbo.Employees

  ADD CONSTRAINT PK_Employees

  PRIMARY KEY(empid);

/*
- Primary Key enforces the uniqueness of rows.
- Each table can only have one primary key.
*/

/*
### **Unique Constraints**
*/

ALTER TABLE dbo.Employees

  ADD CONSTRAINT UNQ_Employees_ssn

  UNIQUE(ssn);

/*
- It enforces uniquness of rows
- Can define multiple unique constraints in a same table
*/

/*
### **Foreign-key Constraints**
*/

DROP TABLE IF EXISTS dbo.Orders;



CREATE TABLE dbo.Orders

(

    orderid INT         NOT NULL,

    empid   INT         NOT NULL,

    custid  VARCHAR(30) NOT NULL,

    orderts DATETIME2   NOT NULL,

    qty     INT         NOT NULL,

    CONSTRAINT PK_Orders

      PRIMARY KEY(orderid)

);

ALTER TABLE dbo.Orders

  ADD CONSTRAINT FK_Orders_Employees

  FOREIGN KEY(empid)

  REFERENCES dbo.Employees(empid);

/*
_To restrict mgrid column to the values in empid column_
*/

ALTER TABLE dbo.Employees

  ADD CONSTRAINT FK_Employees_Employees

  FOREIGN KEY(mgrid)

  REFERENCES dbo.Employees(empid);

/*
### **Check Constraints**
*/

ALTER TABLE dbo.Employees

  ADD CONSTRAINT CHK_Emplouee_salary

  CHECK(salary>0.00);

/*
- to define a predicate that a row must meet certain criteria.
*/

/*
### **Default Constraints**
*/

ALTER TABLE dbo.Orders

  ADD CONSTRAINT DFT_Orders_orderts

  DEFAULT(SYSDATETIME()) FOR orderts;

DROP TABLE IF EXISTS dbo.Orders, dbo.Employees;
